import { useLocation } from "react-router-dom";

function Card({product}) { 

    const Location = useLocation();
    if(Location.pathname === '/MainUser'){
    return(
        <div className="product-card">
        <div className="card-image">
            <img src={product.image} alt="Product Image"></img>
        </div>
        <div className="card-details">
            <h3 className="product-title">{product.name}</h3>
            <p className="product-description">{product.description}</p>
            <div className="card-footer">
                <span className="product-price">{product.price}</span>
                <button className="add-to-cart">Add to Cart</button>
            </div>
        </div>
    </div>
    );}else if (Location.pathname === '/Search'){
        return(
        <div className="product-card">
        <div className="card-image">
            <img src={product.image} alt="Product Image"></img>
        </div>
        <div className="card-details">
            <div className="product-header">
            <h3 className="product-title">Youssef</h3>
            <p className="product-description">{product.description}</p>
            </div>
            <div className="card-footer">
                <span className="product-price">{product.price}</span>
                <button className="add-to-cart">Add to Cart</button>
            </div>
        </div>
    </div>)
    }
}


function ProductList() {

    // const [products, setProducts] = useState([]);

    // useEffect(() => {
    //     // Simulate an API call
    //     fetch('/api/products')
    //         .then((response) => response.json())
    //         .then((data) => setProducts(data));
    // }, []);

    const ids = [1, 2, 3, 4, 5, 6, 7, 8, 9];

    // Flatten the array of products
    const products = ids.flatMap(id => [
      { id: id, name: `Product A-${id}`, price: 29.99, image: '/Imgs/instagram-06.jpg', description: `Description of product ${id}` }
     
    ]);
    
    return (
      <div className="product-list mainuser">
        {products.map(product => ( 
          <Card key={product.id} product={product} /> // Each product is passed as an individual prop
        ))}
      </div>
    );
}
export default {Card ,ProductList }
