import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ProductsList2 = () => {
    const [products, setProducts] = useState([]); // State to store fetched products
    const [loading, setLoading] = useState(true); // State to handle loading

    useEffect(() => {
        // Fetch products from the backend
        const fetchProducts = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/products'); // Backend URL
                setProducts(response.data); // Store data in state
            } catch (error) {
                console.error('Error fetching products:', error);
            } finally {
                setLoading(false); // Stop loading
            }
        };

        fetchProducts();
    }, []); // Empty dependency array means this runs once on component mount

    // If still loading, show a spinner or message
    if (loading) return <div>Loading products...</div>;

    return (
        <div>
            <h1>Products List</h1>
            <ul>
                {products.map((product) => (
                    <li key={product.product_id}>
                        <h2>{product.name}</h2>
                        <p>Price: ${product.price}</p>
                        <img src={product.image_url} alt={product.name} style={{ width: '100px' }} />
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ProductsList2;
