import React, { useState } from 'react';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import './App.css';
import Home from './Guest_Page';
import Registering from './Registering-page';
import MainPage from './Main_User_Page';
import SearchResults from './results_page';
import Product from './ProductPage';
import CartPage from './Cart';
import ProfilePage from './ProfilePage';
import { UserProvider } from './UserContext';

import UserDashboard from './UserDashboard';
import Rewards from './Rewards';
import RedeemReward from './RedeemReward';

const App = () => {
  const [userId] = useState(1); // Assume user ID 1 for now
  const [selectedRewardId, setSelectedRewardId] = useState(null);

  const handleRewardSelect = (rewardId) => {
    setSelectedRewardId(rewardId);
  };

  return (
    <UserProvider>
      <BrowserRouter>
        <Routes>
          {/* Guest Pages */}
          <Route path="/" element={<Home />} />
          <Route path="/Login" element={<Registering.LoginForm />} />
          <Route path="/SignUp" element={<Registering.SignUpForm />} />

          {/* Main User Pages */}
          <Route path="/MainUser" element={<MainPage />} />
          <Route path="/Search" element={<SearchResults />} />
          <Route path="/Product" element={<Product />} />
          <Route path="/Cart" element={<CartPage />} />
          <Route path="/Profile" element={<ProfilePage />} />

          {/* Reward Points System */}
          <Route
            path="/Rewards"
            element={
              <div className="App">
                <h1>Reward Points System</h1>
                <UserDashboard userId={userId} />
                <Rewards onRedeem={handleRewardSelect} />
                {selectedRewardId && (
                  <div>
                    <h3>Redeem Reward</h3>
                    <RedeemReward userId={userId} rewardId={selectedRewardId} />
                  </div>
                )}
              </div>
            }
          />
        </Routes>
      </BrowserRouter>
    </UserProvider>
  );
};

export default App;
