import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Card({ product }) {
  const [inCart, setInCart] = useState(false);
  const [inWishlist, setInWishlist] = useState(false);
  const navigate = useNavigate();

  // Fetch cart and wishlist status when component loads
  useEffect(() => {
    const fetchStatus = async () => {
      try {
        const cartResponse = await axios.get(`http://localhost:5000/api/cart/status/${product.id}`);
        setInCart(cartResponse.data.inCart);

        const wishlistResponse = await axios.get(`http://localhost:5000/api/wishlist/status/${product.id}`);
        setInWishlist(wishlistResponse.data.inWishlist);
      } catch (error) {
        console.error('Error fetching status:', error);
      }
    };

    fetchStatus();
  }, [product.id]);

  const handleAddToCart = async () => {
    try {
      if (!inCart) {
        await axios.post(`http://localhost:5000/api/cart/${product.id}`, { productId: product.id });
        setInCart(true);
        alert('Product added to cart!');
      } else {
        navigate('/cart');
      }
    } catch (error) {
      console.error('Error adding to cart:', error);
    }
  };

  const handleAddToWishlist = async () => {
    try {
      if (!inWishlist) {
        await axios.post(`http://localhost:5000/api/wishlist`, { productId: product.id });
        setInWishlist(true);
        alert('Product added to wishlist!');
      } else {
        navigate('/wishlist');
      }
    } catch (error) {
      console.error('Error adding to wishlist:', error);
    }
  };

  const handleCardClick = () => {
    navigate(`/product/${product.id}`);
  };

  return (
    <div className="product-card" onClick={handleCardClick}>
      <div className="card-image">
        <img
          src={product.image_url}
          alt="Product Image"
          onClick={(e) => e.stopPropagation()} // Prevent triggering card click
        />
      </div>
      <div className="card-details">
        <h3 className="product-title">{product.name}</h3>
        <p className="product-description">{product.description}</p>
        <div className="card-footer">
          <span className="product-price">${product.price}</span>
          <button
            className="add-to-cart"
            onClick={(e) => {
              e.stopPropagation();
              handleAddToCart();
            }}
          >
            {inCart ? 'View Cart' : 'Add to Cart'}
          </button>
          <button
            className="add-to-wishlist"
            onClick={(e) => {
              e.stopPropagation();
              handleAddToWishlist();
            }}
          >
            {inWishlist ? 'View Wishlist' : 'Add to Wishlist'}
          </button>
        </div>
      </div>
    </div>
  );
}

export default Card;
