// ProductList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Card from './Card';

const ProductList = () => {
const [products, setProducts] = useState([]);

useEffect(() => {
const fetchProducts = async () => {
    try {
    const response = await axios.get('http://localhost:5000/api/products');
    setProducts(response.data);
    console.log(products.image_url)

    } catch (error) {
    console.error('Error fetching products:', error);
    }
};

fetchProducts();
}, []);



const handleAddToCart = async (productId) => {
try {
    await axios.post('http://localhost:5000/api/products/add-to-cart', { productId });
    alert('Product added to cart');
} catch (error) {
    console.error('Error adding to cart:', error);
}
};

const handleAddToWishlist = async (productId) => {
try {
    await axios.post('http://localhost:5000/api/products/add-to-wishlist', { productId });
    alert('Product added to wishlist');
} catch (error) {
    console.error('Error adding to wishlist:', error);
}
};

const handleViewProduct = (productId) => {
console.log(`Navigate to product ${productId}`);
// Example: Use React Router to navigate
// navigate(`/product/${productId}`);
};


return (
<div className="product-list">
    {products.map((product, index) => (
    <Card
        key={product.id || index} // Fallback to index if id is unavailable
        product={product}
        img = {product.image}
        onAddToCart={handleAddToCart}
        onAddToWishlist={handleAddToWishlist}
        onViewProduct={handleViewProduct}
    />
    ))}
</div>
);


};

export default ProductList;
